package bootcampfinal;

public class bootcampMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("this is the result of subtraction  "+ subtraction(300, 200));
		System.out.println("this is the result of addition  " + addition(400, 500));
		
		
	}
	
	public static int subtraction (int a, int b)
	{
		int c = a-b;
		return c;
		
	}
	public static int addition (int a, int b)
	{
		int c = a+b;
		return c;
		
	}

}
